console.log("begin filter at", new Date().getTime())
// Get the static data of the node
const globalStaticData = $getWorkflowStaticData('global');

// Access its data
const lastExecutionTimestamp = globalStaticData.lastExecutionTimestamp;
console.log("Get lastExecutionTimestamp", lastExecutionTimestamp || "")

let items = $input.all();
let itemsNeedPush = [];
if (lastExecutionTimestamp) {
  for (const item of items) {
    itemTimestamp = new Date(item.json.isoDate).getTime();
    if( itemTimestamp < lastExecutionTimestamp) {
      console.log("item %s < %s, drop: %s", itemTimestamp, lastExecutionTimestamp, item.json.title)
      continue
    }
    console.log("item %s > %s, push: %s", itemTimestamp, lastExecutionTimestamp, item.json.title)
    itemsNeedPush.push(item);
  }
} else {
  // 首次运行，推送三条消息即可
  itemsNeedPush = items.slice(0, items.length > 3 ? 3 : items.length);
}

// Update lastExecution
let newlastExecutionTimestamp = new Date().getTime();
globalStaticData.lastExecutionTimestamp = newlastExecutionTimestamp;
console.log("Set lastExecutionTimestamp to", newlastExecutionTimestamp);
// 按照时间正序排序
return itemsNeedPush.reverse();

