let results = []
let i = 0;

do {
  try {
    results = results.concat($('RSS Feed Read').all(0, i));
  } catch (error) {
    return results;
  }
  i++;
} while (true);

function extractIdFromUrl(url) {
  const regex = /\/status\/(\d+)/;
  const match = url.match(regex);
  if (match) {
    return match[1];
  } else {
    return null;
  }
}
for (const item of $input.all()) {
  item.json.tweetId = extractIdFromUrl(item.json.guid);
}

return $input.all();

function timestampToHuman(timestampInMillis) {
    return DateTime.fromMillis(timestampInMillis).toISO();
}


console.log("begin filter at", $now.toISO());
// Get the static data of the node
const globalStaticData = $getWorkflowStaticData('global');

// Access its data
const lastExecutionTimestamp = globalStaticData.lastExecutionTimestamp;
console.log("Get lastExecutionTimestamp", lastExecutionTimestamp ? timestampToHuman(lastExecutionTimestamp) : "")

let items = $input.all();
let itemsNeedPush = [];
if (lastExecutionTimestamp) {
  for (const item of items) {
    itemTimestamp = new Date(item.json.isoDate).getTime();
    if( itemTimestamp < lastExecutionTimestamp) {
      console.log("item %s < %s, drop: %s", timestampToHuman(itemTimestamp), timestampToHuman(lastExecutionTimestamp), item.json.title)
      continue
    }
    console.log("item %s > %s, push: %s", timestampToHuman(itemTimestamp), timestampToHuman(lastExecutionTimestamp), item.json.title)
    itemsNeedPush.push(item);
  }
} else {
  // 首次运行，推送三条消息即可
  itemsNeedPush = items.slice(0, items.length > 3 ? 3 : items.length);
}

// Update lastExecution
let newlastExecutionTimestamp = new Date().getTime();
globalStaticData.lastExecutionTimestamp = newlastExecutionTimestamp;
console.log("Set lastExecutionTimestamp to", timestampToHuman(newlastExecutionTimestamp));
// 按照时间正序排序
return itemsNeedPush.reverse();

